import React, { useEffect, useRef } from 'react';
import './App.css';
import * as d3 from 'd3';

function App() {
  const d3Chart = useRef();

  const data = [
    { skill: 'CSS', value: 80 },
    { skill: 'HTML', value: 70 },
    { skill: 'JS', value: 85 },
    { skill: 'ANGULAR', value: 90 },
    { skill: 'REACT', value: 75 },
    { skill: 'D3', value: 70 },
    { skill: 'NODE JS', value: 65 },
    { skill: 'JAVA', value: 65 },
    { skill: 'UI DESIGN', value: 70 },
    { skill: 'XD', value: 65 },
  ];
  useEffect(() => {
    let yAxisAttribute = 'skill';
    let xAxisAttribute = 'value';
    let width = 1000;
    let height = 400;

    let margin = { top: 20, right: 30, bottom: 40, left: 90 },
      w = width - margin.left - margin.right,
      h = height - margin.top - margin.bottom;

    let svg = d3
      .select('.App')
      .append('svg')
      .attr('width', w + margin.left + margin.right)
      .attr(h + margin.top + margin.bottom)
      .append('g')
      .attr('transform', 'translate(' + margin.left + ',' + margin.top + ')');

    let x = d3.scaleLinear().domain([0, 100]).range([0, width]);
    svg
      .append('g')
      .attr('transform', 'translate(0,' + height + ')')
      .attr('class', 'axis x')
      .call(d3.axisBottom(x))
      .selectAll('text')
      .attr('transform', 'translate(-10,0)rotate(-45)')
      .style('text-anchor', 'end');
    // Add Y axis
    let y = d3
      .scaleBand()
      .range([0, height])
      .domain(this.state.data.map((d) => d[this.state.yAxisAttribute]))
      .padding(0.1);
    svg
      .append('g')
      .attr('class', 'axis y')
      .call(d3.axisLeft(y))
      .selectAll('text')
      .attr('dy', null);
    // Add Bars
    svg
      .selectAll('myRect')
      .data(this.state.data)
      .enter()
      .append('rect')
      .on('mouseover', function () {
        d3.select(this).style('opacity', 0.5);
      })
      .on('mouseout', function () {
        d3.select(this).style('opacity', 1);
      })
      .attr('x', x(0))
      .attr('y', (d) => y(d[this.state.yAxisAttribute]))
      .attr('width', 0)
      .attr('height', y.bandwidth() - 10)
      .attr('fill', '#DF337D')
      .transition(d3.transition().duration(1000))
      .attr('width', (d) => x(d[this.state.xAxisAttribute]));
  });

  return (
    <div className='App'>
      <svg ref={d3Chart} />
      <h1>Hello</h1>
    </div>
  );
}

export default App;
